SELECT eixo_1.number_of_records FROM eixo_1 WHERE ((eixo_1.municipio_do_local_da_oferta = 'Uberlndia') AND (eixo_1.subtipo_curso = 'FIC') AND (eixo_1.uf_do_local_da_oferta = 'MG') AND (CAST(EXTRACT(YEAR FROM eixo_1.data_de_inicio) AS LONG) IN (2011, 2012, 2013, 2014)) AND (NOT ((eixo_1.nome_da_sit_matricula_situacao_detalhada IN ('CANC_DESISTENTE', 'CANC_MAT_PRIM_OPCAO', 'CANC_SANO', 'CANC_SEM_FREQ_INICIAL', 'CANC_TURMA', 'DOC_INSUFIC', 'ESCOL_INSUFIC', 'INC _ITINERARIO', 'INSC_CANC', 'No Matriculado', 'NO_COMPARECEU', 'TURMA_CANC', 'VAGAS_INSUFIC')) OR (eixo_1.nome_da_sit_matricula_situacao_detalhada IS NULL))) AND (NOT (eixo_1.situacao_da_turma IN ('CANCELADA', 'CRIADA', 'PUBLICADA'))));
