SELECT generico_5.number_of_records FROM generico_5 WHERE ((generico_5.medio = 'RADIO') AND (CAST(EXTRACT(YEAR FROM generico_5.fecha) AS LONG) IN (2014, 2015)) AND (generico_5.anunciante IN ('BANTRAB/TODOTICKET', 'TODOTICKET', 'TODOTICKET.COM')) AND (CAST(EXTRACT(MONTH FROM generico_5.fecha) AS LONG) IN (1, 2, 3, 4, 5)));
