SELECT generico_5.number_of_records FROM generico_5 WHERE ((generico_5.anunciante IN ('BANTRAB/TODOTICKET', 'TODOTICKET', 'TODOTICKET.COM')) AND (((CAST(EXTRACT(MONTH FROM generico_5.fecha) AS LONG) >= 2) AND (CAST(EXTRACT(MONTH FROM generico_5.fecha) AS LONG) <= 12)) OR (CAST(EXTRACT(MONTH FROM generico_5.fecha) AS LONG) IS NULL)) AND (CAST(EXTRACT(YEAR FROM generico_5.fecha) AS LONG) = 2015) AND (generico_5.medio = 'TELEVISION NACIONAL'));
