SELECT motos_2.marca AS datos_copia,   motos_2.soporte AS soporte,   motos_2.vehiculo AS vehiculo,   SUM(motos_2.inversionus) AS suminversionusok,   SUM(CAST(motos_2.numanuncios AS LONG)) AS sumnumanunciosok,   CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) AS yrfechaok FROM motos_2 WHERE ((CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) = 2015) AND (motos_2.categoria = 'MOTOCICLETAS') AND (motos_2.medio = 'TELEVISION NACIONAL')) GROUP BY motos_2.marca,   motos_2.soporte,   motos_2.vehiculo,   yrfechaok;
