SELECT motos_1.number_of_records FROM motos_1 WHERE ((motos_1.categoria IN ('CAMIONES', 'CAMIONES, BUSES Y PANELES', 'MOTOCICLETAS', 'PICK UPS, VANS Y JEEPS', 'PICK-UPS', 'SUV Y JEEPS', 'VEHICULOS NUEVOS')) AND (CAST(EXTRACT(YEAR FROM motos_1.fecha) AS LONG) >= 2010) AND (CAST(EXTRACT(YEAR FROM motos_1.fecha) AS LONG) <= 2015));
