SELECT SUM(motos_2.inversionus) AS temptc_26225288700,   CAST(EXTRACT(MONTH FROM motos_2.fecha) AS LONG) AS mnfechaok,   SUM(motos_2.inversionus) AS sumcalculation_0061002123102817ok,   CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) AS yrfechaok FROM motos_2 WHERE ((CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) >= 2010) AND (CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) <= 2015) AND (CAST(EXTRACT(MONTH FROM motos_2.fecha) AS LONG) >= 1) AND (CAST(EXTRACT(MONTH FROM motos_2.fecha) AS LONG) <= 9) AND (motos_2.categoria = 'MOTOCICLETAS') AND (motos_2.marca IN ('MOTOCICLETAS YAMAHA', 'YAMAHA'))) GROUP BY mnfechaok,   yrfechaok;
