SELECT SUM(motos_2.inversionus) AS sumcalculation_0061002123102817ok,   SUM(motos_2.inversionus) AS suminversionusok,   CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) AS yrfechaok FROM motos_2 WHERE ((CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) >= 2010) AND (CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) <= 2015) AND (motos_2.categoria IN ('CAMIONES', 'CAMIONES, BUSES Y PANELES', 'MOTOCICLETAS', 'PICK UPS, VANS Y JEEPS', 'PICK-UPS', 'SUV Y JEEPS', 'VEHICULOS NUEVOS')) AND (motos_2.categoria = 'MOTOCICLETAS') AND (motos_2.marca IN ('MOTOCICLETAS YAMAHA', 'YAMAHA')) AND (CAST(EXTRACT(MONTH FROM motos_2.fecha) AS LONG) >= 1) AND (CAST(EXTRACT(MONTH FROM motos_2.fecha) AS LONG) <= 9)) GROUP BY yrfechaok;
