SELECT motos_1.marca AS datos_copia,   SUM(motos_1.inversionus) AS temptc_26225288700,   SUM(motos_1.inversionus) AS sumcalculation_0061002123102817ok,   CAST(EXTRACT(YEAR FROM motos_1.fecha) AS LONG) AS yrfechaok FROM motos_1 WHERE ((CAST(EXTRACT(YEAR FROM motos_1.fecha) AS LONG) >= 2010) AND (CAST(EXTRACT(YEAR FROM motos_1.fecha) AS LONG) <= 2015) AND (motos_1.categoria = 'MOTOCICLETAS')) GROUP BY motos_1.marca,   yrfechaok;
