SELECT motos_2.number_of_records FROM motos_2 WHERE ((CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) >= 2010) AND (CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) <= 2015) AND ('Cerrar' = 'Cerrar') AND (CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) = 2015) AND (CAST(EXTRACT(MONTH FROM motos_2.fecha) AS LONG) IN (9, 10, 11)) AND (motos_2.categoria = 'MOTOCICLETAS'));
