SELECT motos_2.anunciante_grupo AS anunciante_grupo,   motos_2.marca AS datos_copia,   motos_2.medio AS medio,   motos_2.primeralinea AS primeralinea,   motos_2.ver_anuncio AS ver_anuncio,   motos_2.vehiculo AS vehiculo,   CAST(EXTRACT(MONTH FROM motos_2.fecha) AS LONG) AS mnfechaok,   SUM(CAST(motos_2.numanuncios AS LONG)) AS sumnumanunciosok,   CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) AS yrfechaok FROM motos_2 WHERE ((CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) >= 2010) AND (CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) <= 2015) AND ('Cerrar' = 'Cerrar') AND (CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) = 2015) AND (CAST(EXTRACT(MONTH FROM motos_2.fecha) AS LONG) IN (9, 10, 11)) AND (motos_2.categoria = 'MOTOCICLETAS')) GROUP BY motos_2.anunciante_grupo,   motos_2.marca,   motos_2.medio,   motos_2.primeralinea,   motos_2.ver_anuncio,   motos_2.vehiculo,   mnfechaok,   yrfechaok;
