SELECT motos_2.marca AS datos_copia,   motos_2.medio AS medio,   SUM(motos_2.inversionus) AS sumcalculation_0061002123102817ok FROM motos_2 WHERE ((motos_2.categoria IN ('CAMIONES', 'CAMIONES, BUSES Y PANELES', 'MOTOCICLETAS', 'PICK UPS, VANS Y JEEPS', 'PICK-UPS', 'SUV Y JEEPS', 'VEHICULOS NUEVOS')) AND (CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) >= 2010) AND (CAST(EXTRACT(YEAR FROM motos_2.fecha) AS LONG) <= 2015) AND (motos_2.categoria = 'MOTOCICLETAS')) GROUP BY motos_2.marca,   motos_2.medio;
