SELECT mulheresmil_1.number_of_records FROM mulheresmil_1 WHERE ((mulheresmil_1.uf_do_local_da_oferta = 'PE') AND (NOT ((mulheresmil_1.nome_da_sit_matricula_situacao_detalhada NOT IN ('', 'TRANSF_EXT', 'INTEGRALIZADA', 'FREQ_INIC_INSUF', 'TRANCADA', 'CONCLUDA', 'TRANSF_INT', 'EM_CURSO', 'REPROVADA', 'ABANDONO', 'CONFIRMADA', 'EM_DEPENDNCIA')) OR (mulheresmil_1.nome_da_sit_matricula_situacao_detalhada IS NULL))) AND (NOT (mulheresmil_1.situacao_da_turma IN ('CANCELADA', 'CRIADA', 'PUBLICADA'))));
