SELECT mulheresmil_1.number_of_records FROM mulheresmil_1 WHERE ((mulheresmil_1.no_modalidade = 'Pronatec Mulheres Mil') AND (NOT ((mulheresmil_1.nome_da_sit_matricula_situacao_detalhada IN ('AGUARD_CONF', 'CANC_DESISTENTE', 'CANC_MAT_PRIM_OPCAO', 'CANC_SANO', 'CANC_SEM_FREQ_INICIAL', 'CANC_TURMA', 'DOC_INSUFIC', 'ESCOL_INSUFIC', 'INC _ITINERARIO', 'INSC_CANC', 'NO_COMPARECEU', 'TURMA_CANC', 'VAGAS_INSUFIC')) OR (mulheresmil_1.nome_da_sit_matricula_situacao_detalhada IS NULL))) AND (NOT (mulheresmil_1.situacao_da_turma IN ('CANCELADA', 'CRIADA', 'PUBLICADA'))) AND (mulheresmil_1.uf_do_local_da_oferta >= 'AC') AND (mulheresmil_1.uf_do_local_da_oferta <= 'TO'));
