SELECT mulheresmil_1.number_of_records FROM mulheresmil_1 WHERE ((mulheresmil_1.no_modalidade IN ('pronatec turismo cidado', 'Pronatec turismo Desenvolvimento Local', 'PRONATEC TURISMO na EMPRESA', 'PRONATEC TURISMO SOCIAL')) AND (NOT ((mulheresmil_1.nome_da_sit_matricula_situacao_detalhada NOT IN ('', 'TRANSF_EXT', 'INTEGRALIZADA', 'FREQ_INIC_INSUF', 'TRANCADA', 'CONCLUDA', 'TRANSF_INT', 'EM_CURSO', 'REPROVADA', 'ABANDONO', 'CONFIRMADA', 'EM_DEPENDNCIA')) OR (mulheresmil_1.nome_da_sit_matricula_situacao_detalhada IS NULL))) AND ((CAST(EXTRACT(YEAR FROM mulheresmil_1.data_de_inicio) AS LONG) IS NULL) OR ((CAST(EXTRACT(YEAR FROM mulheresmil_1.data_de_inicio) AS LONG) >= 1201) AND (CAST(EXTRACT(YEAR FROM mulheresmil_1.data_de_inicio) AS LONG) <= 3013))) AND (NOT (mulheresmil_1.situacao_da_turma IN ('CANCELADA', 'CRIADA', 'PUBLICADA'))));
