SELECT mulheresmil_1.no_modalidade AS no_modalidade,   mulheresmil_1.nome_do_curso AS nome_do_curso,   SUM(CAST(mulheresmil_1.number_of_records AS LONG)) AS sumnumber_of_recordsok FROM mulheresmil_1 WHERE ((mulheresmil_1.no_modalidade = 'Pronatec Brasil sem Misria') AND (NOT ((mulheresmil_1.nome_da_sit_matricula_situacao_detalhada NOT IN ('', 'TRANSF_EXT', 'INTEGRALIZADA', 'FREQ_INIC_INSUF', 'TRANCADA', 'CONCLUDA', 'TRANSF_INT', 'EM_CURSO', 'REPROVADA', 'ABANDONO', 'CONFIRMADA', 'EM_DEPENDNCIA')) OR (mulheresmil_1.nome_da_sit_matricula_situacao_detalhada IS NULL))) AND (NOT (mulheresmil_1.situacao_da_turma IN ('CANCELADA', 'CRIADA', 'PUBLICADA')))) GROUP BY mulheresmil_1.no_modalidade,   mulheresmil_1.nome_do_curso;
