SELECT AVG(CAST(CAST(realestate1_1.price AS LONG) AS double)) AS avgpriceok,   CAST(EXTRACT(YEAR FROM realestate1_1.date_of_transfer) AS LONG) AS yrdate_of_transferok FROM realestate1_1 WHERE ((CAST(EXTRACT(YEAR FROM realestate1_1.date_of_transfer) AS LONG) IN (2005, 2006, 2007, 2008, 2009, 2010, 2011, 2012, 2013, 2014)) AND (CAST(realestate1_1.date_of_transfer as DATE) >= date '2005-01-01') AND (CAST(realestate1_1.date_of_transfer as DATE) <= date '2015-03-31')) GROUP BY yrdate_of_transferok;
