SELECT eixo_1.no_modalidade AS no_modalidade,   eixo_1.nome_da_sit_matricula_situacao_detalhada AS nome_da_sit_matricula_situacao_detalhada,   SUM(CAST(eixo_1.number_of_records AS LONG)) AS sumnumber_of_recordsok,   CAST(EXTRACT(YEAR FROM eixo_1.data_de_inicio) AS LONG) AS yrdata_de_iniciook FROM eixo_1 WHERE ((eixo_1.no_modalidade IN ('pronatec turismo cidado', 'Pronatec turismo Desenvolvimento Local', 'PRONATEC TURISMO na EMPRESA', 'PRONATEC TURISMO SOCIAL')) AND (NOT ((eixo_1.nome_da_sit_matricula_situacao_detalhada NOT IN ('', 'TRANSF_EXT', 'INTEGRALIZADA', 'FREQ_INIC_INSUF', 'TRANCADA', 'CONCLUDA', 'TRANSF_INT', 'EM_CURSO', 'REPROVADA', 'ABANDONO', 'CONFIRMADA', 'EM_DEPENDNCIA')) OR (eixo_1.nome_da_sit_matricula_situacao_detalhada IS NULL))) AND ((CAST(EXTRACT(YEAR FROM eixo_1.data_de_inicio) AS LONG) IS NULL) OR ((CAST(EXTRACT(YEAR FROM eixo_1.data_de_inicio) AS LONG) >= 1201) AND (CAST(EXTRACT(YEAR FROM eixo_1.data_de_inicio) AS LONG) <= 3013))) AND (NOT (eixo_1.situacao_da_turma IN ('CANCELADA', 'CRIADA', 'PUBLICADA')))) GROUP BY eixo_1.no_modalidade,   eixo_1.nome_da_sit_matricula_situacao_detalhada, yrdata_de_iniciook;
