SELECT generico_5.categoria AS categoria FROM generico_5 WHERE ((generico_5.anunciante IN ('BANTRAB/TODOTICKET', 'TODOTICKET', 'TODOTICKET.COM')) AND (CAST(EXTRACT(YEAR FROM generico_5.fecha) AS LONG) >= 2010) AND (CAST(EXTRACT(YEAR FROM generico_5.fecha) AS LONG) <= 2015) AND (((CAST(EXTRACT(MONTH FROM generico_5.fecha) AS LONG) >= 1) AND (CAST(EXTRACT(MONTH FROM generico_5.fecha) AS LONG) <= 7)) OR (CAST(EXTRACT(MONTH FROM generico_5.fecha) AS LONG) IS NULL))) GROUP BY generico_5.categoria;
