SELECT generico_5.vehiculo AS vehiculo,   CAST(EXTRACT(MONTH FROM generico_5.fecha) AS LONG) AS mnfechaok,   CAST(EXTRACT(YEAR FROM generico_5.fecha) AS LONG) AS yrfechaok FROM generico_5 WHERE ((CAST(EXTRACT(YEAR FROM generico_5.fecha) AS LONG) >= 2010) AND (CAST(EXTRACT(YEAR FROM generico_5.fecha) AS LONG) <= 2015) AND (generico_5.anunciante IN ('BANTRAB/TODOTICKET', 'TODOTICKET', 'TODOTICKET.COM')) AND (generico_5.medio = 'TELEVISION NACIONAL')) GROUP BY generico_5.vehiculo, mnfechaok, yrfechaok;
