SELECT uberlandia_1.no_modalidade AS no_modalidade,   uberlandia_1.nome_do_curso AS nome_do_curso,   SUM(CAST(uberlandia_1.number_of_records AS LONG)) AS sumnumber_of_recordsok FROM uberlandia_1 WHERE ((uberlandia_1.no_modalidade = 'Pronatec Brasil sem Misria') AND (NOT ((uberlandia_1.nome_da_sit_matricula_situacao_detalhada NOT IN ('', 'TRANSF_EXT', 'INTEGRALIZADA', 'FREQ_INIC_INSUF', 'TRANCADA', 'CONCLUDA', 'TRANSF_INT', 'EM_CURSO', 'REPROVADA', 'ABANDONO', 'CONFIRMADA', 'EM_DEPENDNCIA')) OR (uberlandia_1.nome_da_sit_matricula_situacao_detalhada IS NULL))) AND (NOT (uberlandia_1.situacao_da_turma IN ('CANCELADA', 'CRIADA', 'PUBLICADA')))) GROUP BY uberlandia_1.no_modalidade,   uberlandia_1.nome_do_curso;
